mapper.update:
UPDATE lesson
lesson_name = #{lessonName:VARCHAR}, lesson_day = #{lessonDay:VARCHAR}, lesson_start_time = #{lessonStartTime:VARCHAR}, lesson_end_time = #{lessonEndTime:VARCHAR}, lesson_place = #{lessonPlace:VARCHAR}, fees = #{fees:NUMERIC}, subject = #{subject:VARCHAR}, lesson_content = #{lessonContent:VARCHAR}
WHERE lesson_code = #{lessonCode}

mapper.select:
SELECT* FROM (SELECT inner_table.*, ROWNUM AS row_seq FROM (SELECT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time,L.lesson_place, L.subject, L.lesson_code FROM LESSON L, USERS U WHERE L.teacher_id = U.user_id AND teacher_id = #{searchId}
AND L.lesson_name like '%${searchKeyword}%'
ORDER BY L.lesson_create_at desc) inner_table WHERE ROWnUM <= #{endRowNum}) inner_table WHERE row_seq BETWEEN #{startRowNum} and #{endRowNum}

SELECT * FROM (SELECT inner_table.*, ROWNUM AS row_seq FROM( SELECT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time, L.lesson_place, L.subject, L.lesson_code, SR.proposal, L.teacher_Id FROM LESSON L, STUDENTS_RECORD SR, USERS U WHERE L.lesson_code = SR.lesson_code and L.teacher_id = U.user_id and SR.student_id = #{searchId}
AND L.lesson_name like '%${searchKeyword}%'
ORDER BY L.lesson_create_at desc)inner_table WHERE ROWNUM < = #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} and #{endRowNum}

 		SELECT COUNT(*)
 		FROM( SELECT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time,L.lesson_place, L.subject, L.lesson_code
			FROM LESSON L, USERS U 
			WHERE L.teacher_id = U.user_id  AND teacher_id = #{searchId}


 					AND lesson_name like '%${searchKeyword}%'

 		 ) countTable

 		SELECT COUNT(*)
 		FROM(  SELECT L.lesson_name, U.user_name, L.lesson_day, L.lesson_start_time, L.lesson_end_time, L.lesson_place, L.subject, L.lesson_code, SR.proposal, L.teacher_Id
			FROM LESSON L, STUDENTS_RECORD SR, USERS U
			WHERE L.lesson_code = SR.lesson_code and L.teacher_id = U.user_id and SR.student_id = #{searchId}


 					AND lesson_name like '%${searchKeyword}%'

 		 ) countTable

select *
from (select inner_table.*, ROWNUM AS row_seq
	FROM (SELECT B.isbn, B.book_Img, B.book_title, B.author, B.publisher, B.book_year, B.book_price, L.lesson_name
		FROM BOOK B, LESSON_BOOK LB, LESSON L
		WHERE B.isbn=LB.isbn AND LB.lesson_code = L.lesson_code and	L.teacher_id = #{searchId}


				AND L.lesson_name like '%${searchKeyword}%'

		 ) inner_table
		WHERE ROWNUM < = #{endRowNum})
WHERE ROW_SEQ BETWEEN #{startRowNum} AND #{endRowNum}

 		SELECT COUNT(*)
